var meta = {};

for(var i=0; i<msg.payload.length; i++){
    meta[i] = [];
    if(msg.payload[i].data != undefined){
        for(var j=0; j<msg.payload[i].data.length; j++){
            var obj = msg.payload[i].data[j];
            meta[i].push(obj.metadata.value);
        }
    }
    var set = new Set(meta[i]);
    meta[i] = Array.from(set);
}

flow.set("meta",meta);
msg.payload = meta;

return msg;

var meta = flow.get("meta");
var counter = {};

for(var l=0; l<msg.payload.length; l++){
    if(msg.payload[l] != undefined && msg.payload[l].data != undefined){
        for(var i=0; i<msg.payload.length; i++){
            counter[i] = {};
            for(var j=0; j<meta[i].length; j++){
                counter[i][meta[i][j]] = {};
                counter[i][meta[i][j]] = 0;
            }

            for(var j=0; j<msg.payload[i].data.length; j++){
                var obj = msg.payload[i].data[j];
                for(var k=0; k<meta[i].length; k++){
                    if(obj.metadata.value == meta[i][k]){
                        counter[i][meta[i][k]] += 1;
                    }
                }
            }
        }
    }
}

msg.payload = counter; 

flow.set("stats",counter);

return msg;

filter = flow.get("filter");

var parentIDs = [];
var i = -1;

for(var prop in filter){
    i += 1;
    parentIDs[i] = [];
    for(var j=0; j<filter[prop].length; j++){
        if(msg.payload[i] != undefined && msg.payload[i].data != undefined){
            for(var k=0; k<msg.payload[i].data.length; k++){
                var obj = msg.payload[i].data[k];
                if(obj != undefined){
                    if(filter[prop][0] == true && obj.metadata.value.search(filter[prop][j]) > -1){
                        for(var l=0; l<obj.parents.length; l++){
                                parentIDs[i].push(obj.parents[l]);
                            }
                    }else{
                        if(obj.metadata.value == filter[prop][j]){
                            for(var l=0; l<obj.parents.length; l++){
                                parentIDs[i].push(obj.parents[l]);
                            }
                        }
                    }
                }
            }
        }
    }
}

msg.payload = parentIDs;

return msg;

var o = [];

for (var i=0; i < msg.payload.length; i++) {
    if (msg.payload[i] != undefined) {
        if (msg.payload[i].subModels != undefined) {
            var obj = {};
            var temp = [];
            for (var j = 0; j < msg.payload[i].subModels.length; j++) {
                for (var k = 0; k < msg.payload[i].subModels[j].data.length; k++) {
                    // merge data arrays as per API calls 
                    temp.push(msg.payload[i].subModels[j].data[k])
                }
            }
            obj.data = temp;
            o.push(obj);
        } else {
            return msg;
        }
    }
}

msg.payload = o;

return msg

operation = flow.get("operation");

if(operation == "union"){
    var union = new Set([].concat(...msg.payload));
