    //     return msg.payload.every(function(a) {
    //         return a.indexOf(v) !== -1;
    //     });
    // });
    var difference = []
    for(var i=0; i<allObjects.length; i++){
        if(union.includes(allObjects[i]) == false){
            difference.push(allObjects[i]);
        }
    }
    result = difference
}


