var rev = []
while (msg.payload.length > 0){
    rev.push(msg.payload.splice(0, Object.keys(filter).length));    // this works
}

// get meta GUIDs, IFC GUIDs
for(var i=0; i<rev.length; i++){
    merged[i] = [];
    for(var j in rev[i][0]["data"]){
        for(var k in rev[i][1]["data"]){
            if(rev[i][1]["data"][j] !== undefined && rev[i][0]["data"][j]["_id"] === rev[i][1]["data"][k]["_id"]){
                o = {};
                o.metaID = rev[i][0]["data"][j]["_id"];
                o.IFCGUID = rev[i][0]["data"][j]["metadata"]["value"];
                o.value = rev[i][1]["data"][k]["metadata"]["value"];
                o.parents = rev[i][0]["data"][j]["parents"];
                o.parents = rev[i][1]["data"][k]["parents"];
                merged[i].push(o)
            }
        }
    }
}

msg.payload = merged;

return msg;

