function getKeyByValue(object, value) {
    return Object.keys(object).find(key => object[key] === value);
}
function createObject(account, ID, modelID, sharedID){
      var o = {};
      o.account = account;
      o.id = ID;
      o.model = modelID;
      o.shared_id = sharedID;
      return o
}

var objects = [];
filtered = flow.get("filteredMesh");
// node.send({payload:filtered})

if (msg.payload != undefined) {
  if (msg.payload.subModels != undefined){
    for(var i=0; i<filtered.length; i++){
        for (var j=0; j<msg.payload.subModels.length; j++){
            var key = getKeyByValue(msg.payload.subModels[j].idMap, filtered[i]);
            if(key != undefined){
                objects.push(createObject(msg.payload.subModels[j].account, key, msg.payload.subModels[j].model, filtered[i]));
                break
            }
        }
    }
  }else{
      for(var i=0; i<filtered.length; i++){
          var key = getKeyByValue(msg.payload.idMap, filtered[i]);
          objects.push(createObject(flow.get("teamspace"), key, flow.get("modelID"), filtered[i]));
      }
  }
}

msg.payload = objects;

return msg;

