function metaDiff(current, arrayOfPrevious){
    GUIDs = [];
    metaIDs = [];
    for(var i=0; i<current.length; i++){
        for(var j =0; j<arrayOfPrevious.length; j++){
            for(var k=0; k<arrayOfPrevious[j].length; k++){
                if(current[i].IFCGUID === arrayOfPrevious[j][k].IFCGUID){
                    if(current[i].value != arrayOfPrevious[j][k].value){
                        if(metaIDs.indexOf(current[i].metaID != -1)){       //can't get rid of duplicates, array>set>array workaround
                            metaIDs.push(current[i].metaID)
                            for(var l=0; l<current[i].parents.length; l++){
                                if(GUIDs.indexOf(current[i].parents[l] != -1)){
                                    GUIDs.push(current[i].parents[l]);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    metaIDs = new Set(metaIDs);
    metaIDs = Array.from(metaIDs);
    GUIDs = new Set(GUIDs);
    GUIDs = Array.from(GUIDs);
    return [metaIDs, GUIDs];
}

