function allProperties(obj,revision,filter){
    allProp = {};
    for(var f in filter){
        allProp[f] = {};
        for(var i in revision){
            for(var v in obj[revision[i]][f]){
                if(allProp[f].hasOwnProperty(v) == false){
                    allProp[f][v] = [];
                }
            }
        }
    }
    return allProp;
}

function merge(allProp, obj){
    for(var filt in allProp){
        for(var prop in allProp[filt]){
            if(obj[filt].hasOwnProperty(prop)){
                allProp[filt][prop].push(obj[filt][prop])
            }else{
                allProp[filt][prop].push(0)
            }
        }
    }
    return allProp;
}

allProp = allProperties(msg.payload,revision,filter);

for(var i in revision){
    merged = merge(allProp,msg.payload[revision[i]]);
}

return {payload:merged};

