}

valid = [];
invalid = [];

for(var i=0; i<msg.payload.data.length; i++){   // loop through all meta objects
    if(msg.inputs.exclude.indexOf(msg.payload.data[i].metadata["IFC Type"]) === -1){   // excluding types
        var hasField = true;
        var isCorrect = true;
        for(var j=0; j<validate.length; j++){       // loop through validation spreadsheet
            param = String(validate[j].col1).replace(/[^\x00-\x7F]/g, "");      // ASCI characters only, in case something dodgy comes from the spreadsheet
            if(Object.keys(msg.payload.data[i].metadata).indexOf(param) === -1){    // check field
                hasField = false;
            }else{
                if(msg.inputs.checkValue){
                    value = String(validate[j].col2).replace(/[^\x00-\x7F]/g, "");      // ASCI characters only, in case something dodgy comes from the spreadsheet
                    node.send({payload:msg.payload.data[i].metadata[param]})
                    if(msg.payload.data[i].metadata[param] != value){      // check value
                        isCorrect = false;
                    }
                }
            }
        }
        if(hasField && isCorrect){  
            for(var k=0; k<msg.payload.data[i].parents.length; k++){
                valid.push(msg.payload.data[i].parents[k]); 
            }
        }else{
            for(var k=0; k<msg.payload.data[i].parents.length; k++){
                invalid.push(msg.payload.data[i].parents[k]);
            }
        }
    }
}

return [{payload:valid},{payload:invalid}];

